// n8n Function node: Decodes Google News RSS article links to original URLs

return items.map(item => {
  const input = item.json;
  let url = input.link;

  try {
    // Only process Google News article links
    const match = url.match(/\/articles\/([A-Za-z0-9\-_]+)/);
    if (match) {
      // Extract base64 part from the URL
      const base64part = match[1];
      // Decode from base64 (URL-safe)
      let decoded = Buffer.from(base64part, 'base64').toString('utf-8');
      // Remove possible binary prefix/suffix (common in Google encoding)
      decoded = decoded.replace(/^[\x08\x13\x22]+/, '').replace(/[\xd2\x01\x00]+$/, '');
      // Find the first http/https URL in the decoded string
      const urlMatch = decoded.match(/https?:\/\/[^\s"]+/);
      if (urlMatch) {
        url = urlMatch[0];
      }
    }
  } catch (e) {
    // If any error, keep the original link
    url = input.link;
  }

  // Output original item with an added 'cleanUrl' property
  return {
    json: {
      ...input,
      cleanUrl: url
    }
  };
});

return items.map(item => {
  const output = item.json.text;
  const titleMatch = output.match(/Title:\s*(.*)/);
  const summaryMatch = output.match(/Summary:\s*([\s\S]*)/);

  return {
    json: {
      title: titleMatch ? titleMatch[1] : '',
      summary: summaryMatch ? summaryMatch[1].trim() : ''
    }
  };
});
